import React, { useState } from 'react';

const categories = [
  'Electronics', 'Clothing', 'Home', 'Beauty', 'Sports', 'Footwear', 'Accessories', 'Books', 'Pets', 'Toys'
];

const styles = [
  'Modern', 'Minimalist', 'Classic', 'Vintage', 'Sporty', 'Professional', 'Casual', 'Luxury'
];

function UserPreferences({ onSubmit }) {
  const [formData, setFormData] = useState({
    categories: [],
    priceRange: [0, 500],
    styles: []
  });

  const handleChange = (e) => {
    const { name, value, checked } = e.target;
    if (name === 'categories' || name === 'styles') {
      setFormData(prev => ({
        ...prev,
        [name]: checked 
          ? [...prev[name], value]
          : prev[name].filter(item => item !== value)
      }));
    } else {
      setFormData(prev => ({ ...prev, [name]: value }));
    }
  };

  const handlePriceChange = (e, index) => {
    const newPriceRange = [...formData.priceRange];
    newPriceRange[index] = parseInt(e.target.value);
    setFormData(prev => ({ ...prev, priceRange: newPriceRange }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <div className="preferences-form">
      <h2>Your Preferences</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Categories:</label>
          <div className="checkbox-group">
            {categories.map(category => (
              <label key={category}>
                <input
                  type="checkbox"
                  name="categories"
                  value={category}
                  checked={formData.categories.includes(category)}
                  onChange={handleChange}
                />
                {category}
              </label>
            ))}
          </div>
        </div>
        
        <div className="form-group">
          <label>Price Range:</label>
          <div className="price-range">
            <input
              type="range"
              min="0"
              max="500"
              value={formData.priceRange[0]}
              onChange={(e) => handlePriceChange(e, 0)}
            />
            <input
              type="range"
              min="0"
              max="500"
              value={formData.priceRange[1]}
              onChange={(e) => handlePriceChange(e, 1)}
            />
            <div className="price-values">
              ${formData.priceRange[0]} - ${formData.priceRange[1]}
            </div>
          </div>
        </div>
        
        <div className="form-group">
          <label>Styles:</label>
          <div className="checkbox-group">
            {styles.map(style => (
              <label key={style}>
                <input
                  type="checkbox"
                  name="styles"
                  value={style}
                  checked={formData.styles.includes(style)}
                  onChange={handleChange}
                />
                {style}
              </label>
            ))}
          </div>
        </div>
        
        <button type="submit">Get Recommendations</button>
      </form>
    </div>
  );
}

export default UserPreferences;
