import React, { useState, useEffect } from 'react';
import { fetchProducts, getRecommendations } from './services/api';
import Catalog from './components/Catalog';
import UserPreferences from './components/UserPreferences';
import Recommendations from './components/Recommendations';
import BrowsingHistory from './components/BrowsingHistory';
import './styles/App.css';

function App() {
  const [products, setProducts] = useState([]);
  const [browsingHistory, setBrowsingHistory] = useState([]);
  const [recommendations, setRecommendations] = useState([]);

  useEffect(() => {
    fetchProducts().then(data => setProducts(data));
  }, []);

  const handleProductClick = (product) => {
    setBrowsingHistory(prev => [...prev, product].slice(-5));
  };

  const handlePreferenceSubmit = (preferences) => {
    getRecommendations({
      preferences,
      browsingHistory: browsingHistory.map(p => p.id)
    }).then(data => setRecommendations(data.recommendations));
  };

  return (
    <div className="app">
      <div className="app-container">
        <div className="left-panel">
          <UserPreferences onSubmit={handlePreferenceSubmit} />
          <BrowsingHistory items={browsingHistory} />
        </div>
        <div className="main-content">
          <Catalog products={products} onProductClick={handleProductClick} />
          <Recommendations recommendations={recommendations} />
        </div>
      </div>
    </div>
  );
}

export default App;
