export const fetchProducts = async () => {
  const response = await fetch('http://localhost:5000/api/products');
  if (!response.ok) {
    throw new Error('Failed to fetch products');
  }
  return response.json();
};

export const submitPreferences = async (preferences) => {
  const response = await fetch('http://localhost:5000/api/preferences', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(preferences),
  });
  if (!response.ok) {
    throw new Error('Failed to submit preferences');
  }
  return response.json();
};

export const submitBrowsingHistory = async (productIds) => {
  const response = await fetch('http://localhost:5000/api/browsing-history', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ productIds }),
  });
  if (!response.ok) {
    throw new Error('Failed to submit browsing history');
  }
  return response.json();
};
